import React, { useEffect, useState } from "react";
import { FaStar } from "react-icons/fa"; // Import star icon

const Manager = () => {
  const [ideas, setIdeas] = useState([]);

  useEffect(() => {
    // Fetch ideas from the backend
    const fetchIdeas = async () => {
      const response = await fetch("http://localhost:3000/api/ideas/ideas");
      const data = await response.json();

      // Add a random AI filter label to each idea
      const ideasWithLabels = data.map((idea) => ({
        ...idea,
        isAIFiltered: Math.random() < 0.5, // Randomly set as true or false
        isRewarded: false, // Track whether the idea has been rewarded or not
        isStarPopped: false, // Track whether the star has been clicked
      }));

      setIdeas(ideasWithLabels);
    };

    fetchIdeas();
  }, []);

  // Handle reward for an idea (UI only change)
  const handleReward = (id) => {
    // Update the ideas array to toggle the rewarded state and animate the star
    setIdeas((prevIdeas) =>
      prevIdeas.map((idea) =>
        idea._id === id
          ? { ...idea, isRewarded: !idea.isRewarded, isStarPopped: true }
          : idea
      )
    );

    // After the animation is completed, reset the pop state after 1s (duration of the animation)
    setTimeout(() => {
      setIdeas((prevIdeas) =>
        prevIdeas.map((idea) =>
          idea.isStarPopped ? { ...idea, isStarPopped: false } : idea
        )
      );
    }, 1000); // Matches the duration of the "pop" animation
  };

  return (
    <>
      <div className="min-h-screen bg-indigo-600">
        {/* Centered Heading */}
        <h1 className="text-3xl font-bold text-white mb-6 text-center">
          Manager Dashboard
        </h1>

        {/* Center the ideas grid */}
        <div className="flex justify-center">
          {/* Idea Cards Grid */}
          <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
            {/* Loop through each idea */}
            {ideas.map((idea) => (
              <div
                key={idea._id}
                className={`flex flex-col p-6 border rounded-md shadow-md bg-white mb-4 transform ${
                  idea.isStarPopped ? "animate-star-pop" : ""
                }`}
                style={
                  {
                    //   transition: "all 0.5s ease-out",
                    //   maxWidth: "350px", // Bigger idea box
                    //   minHeight: "200px", // Increased height for bigger boxes
                  }
                }
              >
                {/* Left: Idea details */}
                <div className="flex-1">
                  <h2 className="text-lg font-semibold">{idea.employeeName}</h2>
                  <p className="text-md mt-2">{idea.idea}</p>

                  {/* Department with indigo background and bold font */}
                  <span
                    className="inline-block mt-2 px-3 py-1 text-white rounded-full font-bold"
                    style={{ backgroundColor: "#4C51BF" }} // indigo-600 background
                  >
                    {idea.department}
                  </span>
                </div>

                {/* Right: Star button and AI Filtered label */}
                <div className="flex flex-col items-center justify-center mt-4">
                  <button
                    onClick={() => handleReward(idea._id)}
                    className={`text-white ${
                      idea.isRewarded ? "bg-yellow-500" : "bg-gray-300"
                    } text-3xl p-4 rounded-full transition duration-300 ease-in-out transform ${
                      idea.isStarPopped ? "animate-star-scale" : ""
                    }`}
                  >
                    <FaStar />
                  </button>
                  <span className="text-lg font-bold text-gray-700 mt-2">
                    {idea.votes} Votes
                  </span>

                  {/* AI Filtered label */}
                  <span
                    className="inline-block mt-2 px-3 py-1 text-white rounded-full font-bold"
                    style={{
                      backgroundColor: idea.isAIFiltered
                        ? "#4C51BF"
                        : "#E53E3E", // Indigo for AI filtered, red for not
                    }}
                  >
                    {idea.isAIFiltered ? "AI Filtered" : "Not AI Filtered"}
                  </span>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* CSS for animation */}
      <style jsx>{`
        @keyframes starPopAnimation {
          0% {
            transform: scale(1); /* Start with the normal size */
          }
          50% {
            transform: scale(1.5); /* Grow to 1.5x the size */
          }
          100% {
            transform: scale(1); /* Return to normal size */
          }
        }

        .animate-star-pop {
          animation: starPopAnimation 1s ease-out; /* Apply the pop effect on star click */
        }

        .animate-star-scale {
          animation: starPopAnimation 0.5s ease-out;
        }
      `}</style>
    </>
  );
};

export default Manager;
