import React from "react";
import { Link } from "react-router-dom"; // Import Link for navigation
import { FaInfoCircle, FaPhoneAlt, FaLightbulb, FaEye } from "react-icons/fa"; // Import icons from react-icons

const Header = () => {
  return (
    <header className="bg-indigo-600 p-4 text-white flex justify-between items-center">
      {/* Website Logo (clickable and takes to home page) */}
      <Link
        to="/"
        className="text-4xl font-bold hover:text-gray-200 flex items-center"
      >
        {/* Optional: Add an image logo here if you have one */}
        <span>Green Future</span>
      </Link>

      {/* Navigation Links with Icons */}
      <nav className="space-x-6 flex items-center">
        {/* About Us */}
        <Link
          to="/about"
          className="text-xl font-bold hover:text-gray-200 flex items-center"
        >
          <FaInfoCircle className="mr-2" /> About Us
        </Link>

        {/* Contact Us */}
        <Link
          to="/contact"
          className="text-xl font-bold hover:text-gray-200 flex items-center"
        >
          <FaPhoneAlt className="mr-2" /> Contact Us
        </Link>

        {/* Idea Submission */}
        <Link
          to="/submit-idea"
          className="text-xl font-bold hover:text-gray-200 flex items-center"
        >
          <FaLightbulb className="mr-2" /> Idea Submission
        </Link>

        {/* View Ideas */}
        <Link
          to="/view"
          className="text-xl font-bold hover:text-gray-200 flex items-center"
        >
          <FaEye className="mr-2" /> View Ideas
        </Link>
      </nav>
    </header>
  );
};

export default Header;
