import React, { useEffect, useState } from "react";
import Header from "./Header";
import { FaThumbsUp } from "react-icons/fa"; // Import thumbs up icon

const View = () => {
  const [ideas, setIdeas] = useState([]);

  useEffect(() => {
    // Fetch ideas from the backend
    const fetchIdeas = async () => {
      const response = await fetch("http://localhost:3000/api/ideas/ideas");
      const data = await response.json();
      setIdeas(data);
    };

    fetchIdeas();
  }, []);

  // Handle vote for an idea
  const handleVote = async (id) => {
    const response = await fetch(`http://localhost:3000/api/ideas/vote/${id}`, {
      method: "PUT",
    });

    if (response.ok) {
      const updatedIdea = await response.json();
      // Update the local state to reflect the new vote count
      setIdeas((prevIdeas) =>
        prevIdeas.map((idea) =>
          idea._id === id ? { ...idea, votes: updatedIdea.votes } : idea
        )
      );
    }
  };

  return (
    <>
      <Header />
      <div className="container mx-auto p-4">
        <h1 className="text-3xl font-bold mb-6">Submitted Ideas</h1>

        <ul className="space-y-4">
          {ideas.map((idea) => (
            <li key={idea._id} className="p-4 border rounded-md shadow-md">
              <h2 className="text-xl font-semibold">{idea.employeeName}</h2>
              <p className="text-md text-gray-700">{idea.idea}</p>
              {/* Department with indigo background and bold font */}
              <span
                className="inline-block mt-2 px-3 py-1 text-white rounded-full font-bold"
                style={{ backgroundColor: "#4C51BF" }} // indigo-600 background
              >
                {idea.department}
              </span>

              {/* Vote button */}
              <div className="mt-4 flex items-center space-x-2">
                <button
                  onClick={() => handleVote(idea._id)}
                  className="text-indigo-600 flex items-center hover:text-indigo-800"
                >
                  <FaThumbsUp className="mr-2" />
                  Vote
                </button>
                <span className="text-lg font-bold text-gray-700">
                  {idea.votes} Votes
                </span>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </>
  );
};

export default View;
