import React, { useEffect, useState } from "react";

const ViewIdeas = () => {
  const [ideas, setIdeas] = useState([]);

  useEffect(() => {
    // Fetch ideas from the backend
    const fetchIdeas = async () => {
      const response = await fetch("http://localhost:3000/api/ideas");
      const data = await response.json();
      setIdeas(data);
    };

    fetchIdeas();
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6">Submitted Ideas</h1>
      <ul className="space-y-4">
        {ideas.map((idea) => (
          <li key={idea.id} className="p-4 border rounded-md shadow-md">
            <h2 className="text-xl font-semibold">{idea.employeeName}</h2>
            <p className="text-md text-gray-700">{idea.idea}</p>
            <span className="text-sm text-gray-500">{idea.department}</span>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ViewIdeas;
