import React from "react";
import { Link } from "react-router-dom"; // Import Link for navigation

const Header = () => {
  return (
    <header className="bg-indigo-600 p-4 text-white flex justify-between items-center">
      {/* Website Logo (clickable and takes to home page) */}
      <Link to="/" className="text-4xl font-bold hover:text-gray-200">
        Green Future
      </Link>

      {/* Navigation Links */}
      <nav className="space-x-6 flex">
        <Link to="/about" className="text-xl font-bold hover:text-gray-200">
          About Us
        </Link>
        <Link to="/contact" className="text-xl font-bold hover:text-gray-200">
          Contact Us
        </Link>
        <Link
          to="/submit-idea"
          className="text-xl font-bold hover:text-gray-200"
        >
          Idea Submission
        </Link>
      </nav>
    </header>
  );
};

export default Header;
