// src/SubmitIdea.js

import React, { useState } from "react";
import Header from "./Header";
import ideaImage from "../Asse"; // Add your image path here

const SubmitIdea = () => {
  const [formData, setFormData] = useState({
    employeeName: "",
    department: "",
    idea: "",
  });
  const [message, setMessage] = useState("");

  const departments = [
    "HR",
    "Sales",
    "Marketing",
    "Development",
    "Design",
    "Support",
  ];

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("Idea submitted successfully!");
    // In a real scenario, you'd send the form data to the backend here.
    setFormData({
      employeeName: "",
      department: "",
      idea: "",
    });
  };

  return (
    <>
      <Header />
      <div className="min-h-screen flex justify-center items-center bg-gray-100">
        {/* Main container with flexbox */}
        <div className="flex w-full max-w-4xl rounded-lg shadow-lg overflow-hidden">
          {/* Left side: Form */}
          <div className="w-full md:w-1/2 flex items-center justify-center bg-white bg-opacity-90 p-6">
            <div className="w-full max-w-md">
              <h1 className="text-2xl font-bold mb-6 text-center">
                Idea Submission
              </h1>
              <form onSubmit={handleSubmit} className="space-y-4">
                <div>
                  <label
                    htmlFor="employeeName"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Employee Name
                  </label>
                  <input
                    type="text"
                    id="employeeName"
                    name="employeeName"
                    value={formData.employeeName}
                    onChange={handleChange}
                    required
                    className="w-full mt-1 p-2 border rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500"
                  />
                </div>

                {/* Department Dropdown */}
                <div>
                  <label
                    htmlFor="department"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Department
                  </label>
                  <select
                    id="department"
                    name="department"
                    value={formData.department}
                    onChange={handleChange}
                    required
                    className="w-full mt-1 p-2 border rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500"
                  >
                    <option value="" disabled>
                      Select Department
                    </option>
                    {departments.map((dept, index) => (
                      <option key={index} value={dept}>
                        {dept}
                      </option>
                    ))}
                  </select>
                </div>

                {/* Idea Text Area */}
                <div>
                  <label
                    htmlFor="idea"
                    className="block text-sm font-medium text-gray-700"
                  >
                    Your Idea
                  </label>
                  <textarea
                    id="idea"
                    name="idea"
                    value={formData.idea}
                    onChange={handleChange}
                    required
                    rows="5"
                    className="w-full mt-1 p-2 border rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500"
                  />
                </div>

                {/* Submit Button */}
                <button
                  type="submit"
                  className="w-full bg-indigo-600 text-white py-2 px-4 rounded-md hover:bg-indigo-700 transition"
                >
                  Submit Idea
                </button>
              </form>

              {message && (
                <div className="mt-4 text-center text-sm text-green-600">
                  {message}
                </div>
              )}
            </div>
          </div>

          {/* Right side: Image */}
          <div
            className="w-1/2 bg-cover bg-center hidden md:block"
            style={{
              backgroundImage: `url(${ideaImage})`,
              height: "100%",
            }}
          ></div>
        </div>
      </div>
    </>
  );
};

export default SubmitIdea;
