// src/SubmitIdea.js

import React, { useState } from "react";
import Header from "./Header";

const SubmitIdea = () => {
  const [formData, setFormData] = useState({
    employeeName: "",
    department: "",
    idea: "",
  });
  const [message, setMessage] = useState("");

  const departments = [
    "HR",
    "Sales",
    "Marketing",
    "Development",
    "Design",
    "Support",
  ];

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    // In a real scenario, you would send formData to a backend here
    console.log(formData);

    // Simulate successful submission
    setMessage("Idea submitted successfully!");
    setFormData({
      employeeName: "",
      department: "",
      idea: "",
    });
  };

  return (
    <>
    <Header />
    <div className="min-h-screen flex items-center justify-center bg-gray-100">

      <div className="w-full max-w-lg bg-white rounded-lg shadow-lg p-6">
        <h1 className="text-2xl font-bold mb-6 text-center">Idea Submission</h1>

        <form onSubmit={handleSubmit} className="space-y-6">
          {/* Employee Name Input */}
          <div>
            <label
              htmlFor="employeeName"
              className="block text-sm font-medium text-gray-700"
            >
              Employee Name
            </label>
            <input
              type="text"
              id="employeeName"
              name="employeeName"
              value={formData.employeeName}
              onChange={handleChange}
              required
              className="w-full mt-1 p-2 border rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500"
            />
          </div>

          {/* Department Dropdown */}
          <div>
            <label
              htmlFor="department"
              className="block text-sm font-medium text-gray-700"
            >
              Department
            </label>
            <select
              id="department"
              name="department"
              value={formData.department}
              onChange={handleChange}
              required
              className="w-full mt-1 p-2 border rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500"
            >
              <option value="" disabled>
                Select Department
              </option>
              {departments.map((dept, index) => (
                <option key={index} value={dept}>
                  {dept}
                </option>
              ))}
            </select>
          </div>

          {/* Idea Text Area */}
          <div>
            <label
              htmlFor="idea"
              className="block text-sm font-medium text-gray-700"
            >
              Your Idea
            </label>
            <textarea
              id="idea"
              name="idea"
              value={formData.idea}
              onChange={handleChange}
              required
              rows="5"
              className="w-full mt-1 p-2 border rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500"
            />
          </div>

          {/* Submit Button */}
          <button
            type="submit"
            className="w-full bg-indigo-600 text-white py-2 px-4 rounded-md hover:bg-indigo-700 transition"
          >
            Submit Idea
          </button>
        </form>

        {/* Success or Error Message */}
        {message && (
          <div className="mt-4 text-center text-sm text-green-600">
            {message}
          </div>
        )}
      </div>
    </div>
    </>
  );
};


export default SubmitIdea;
