import React, { useState, useEffect } from "react";

const Home = () => {
  // Array of image URLs (green/future-themed)
  const images = [
    "https://www.w3schools.com/w3images/forest.jpg", // Image 1
    "https://www.w3schools.com/w3images/woods.jpg", // Image 2
    "https://www.w3schools.com/w3images/mountains.jpg", // Image 3
    "https://www.w3schools.com/w3images/ocean.jpg", // Image 4
    "https://www.w3schools.com/w3images/greenfield.jpg", // Image 5
  ];

  // State to keep track of the current image index
  const [currentIndex, setCurrentIndex] = useState(0);

  // Function to go to the next image
  const nextImage = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % images.length);
  };

  // Auto-rotate images every 3 seconds
  useEffect(() => {
    const intervalId = setInterval(nextImage, 3000); // Change image every 3 seconds
    return () => clearInterval(intervalId); // Clean up interval on component unmount
  }, []);

  return (
    <div className="min-h-screen flex flex-col">
      {/* Header Section */}
      <header className="bg-green-600 p-4 text-white flex justify-between items-center">
        <h1 className="text-3xl font-bold">Green Future</h1>
        <div>
          <input
            type="text"
            placeholder="Search..."
            className="p-2 rounded-lg border border-gray-300"
          />
        </div>
      </header>

      {/* Main Content Section */}
      <main className="flex flex-col md:flex-row justify-center items-center p-6 space-y-6 md:space-y-0 flex-grow">
        {/* Left Image Carousel Section */}
        <div className="w-full md:w-1/2 flex justify-center">
          <img
            src={images[currentIndex]} // Dynamically change the image based on currentIndex
            alt="Green Future"
            className="w-full max-w-4xl rounded-lg shadow-lg"
          />
        </div>

        {/* Right Text Section */}
        <div className="w-full md:w-1/2 text-center md:text-left space-y-4 px-4">
          <h2 className="text-4xl font-extrabold text-green-600">
            A Greener Tomorrow
          </h2>
          <p className="text-xl text-gray-700">
            Join us in creating a sustainable future by protecting our planet.
            Our mission is to raise awareness and provide solutions for a
            cleaner, greener, and healthier world. Together, we can make a
            difference!
          </p>
          <p className="text-lg text-gray-600">
            Letâ€™s create a world where nature and people thrive in harmony. By
            supporting sustainable practices, we take one step closer to a green
            future for all.
          </p>
        </div>
      </main>

      {/* Footer Section */}
      <footer className="bg-green-600 p-4 text-center text-white">
        <p>&copy; 2024 Green Future. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
