import React, { useState, useEffect } from "react";
import axios from "axios";

const Manager = () => {
  const [ideas, setIdeas] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch all ideas for the manager dashboard
  useEffect(() => {
    const fetchIdeas = async () => {
      setLoading(true);
      try {
        // Fetch ideas from the backend
        const response = await axios.get("http://localhost:3000/api/ideas");
        setIdeas(response.data); // Populate ideas from the response
      } catch (error) {
        console.error("Error fetching ideas:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchIdeas();
  }, []); // Empty array means this effect runs once when the component mounts

  // Handle rewarding an idea (mark as rewarded)
  const handleReward = async (ideaId) => {
    try {
      // Reward the idea (this could be another PUT route if needed)
      await axios.put(`http://localhost:3000/api/ideas/${ideaId}`);
      // Update the UI after rewarding the idea
      setIdeas(
        ideas.map((idea) =>
          idea._id === ideaId ? { ...idea, isRewarded: true } : idea
        )
      );
      alert("Idea rewarded successfully!");
    } catch (error) {
      console.error("Error rewarding idea:", error);
      alert("Failed to reward idea.");
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <h1 className="text-3xl font-bold text-center mb-8">Manager Dashboard</h1>

      {loading ? (
        <div>Loading ideas...</div>
      ) : (
        <div className="space-y-6">
          {ideas.map((idea) => (
            <div key={idea._id} className="bg-white p-6 rounded-md shadow-md">
              <h2 className="text-xl font-semibold">{idea.idea}</h2>
              <p className="text-gray-700 mt-2">
                Employee Name: {idea.employeeName} | Department:{" "}
                {idea.department}
              </p>
              <div className="mt-4 flex justify-between items-center">
                <span>
                  Likes: {idea.likes} | AI Filtered:{" "}
                  {idea.isAIFiltered ? "Yes" : "No"}
                </span>
                <button
                  onClick={() => handleReward(idea._id)}
                  className={`px-4 py-2 rounded-md ${
                    idea.isRewarded ? "bg-green-500" : "bg-blue-500"
                  } text-white`}
                  disabled={idea.isRewarded} // Disable if already rewarded
                >
                  {idea.isRewarded ? "Rewarded" : "Reward Idea"}
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Manager;
