import React, { useEffect, useState } from "react";
import { FaStar } from "react-icons/fa"; // Import star icon

const Manager = () => {
  const [ideas, setIdeas] = useState([]);

  useEffect(() => {
    // Fetch ideas from the backend
    const fetchIdeas = async () => {
      const response = await fetch("http://localhost:3000/api/ideas/ideas");
      const data = await response.json();

      // Add a random AI filter label to each idea
      const ideasWithLabels = data.map((idea) => ({
        ...idea,
        isAIFiltered: Math.random() < 0.5, // Randomly set as true or false
        isRewarded: false, // Track whether the idea has been rewarded or not
        isPopped: false, // Track whether the idea box has popped
      }));

      setIdeas(ideasWithLabels);
    };

    fetchIdeas();
  }, []);

  // Handle reward for an idea (UI only change)
  const handleReward = (id) => {
    // Reorder the ideas array to move the clicked idea to the top
    setIdeas((prevIdeas) => {
      const updatedIdeas = prevIdeas.map((idea) =>
        idea._id === id
          ? { ...idea, isRewarded: !idea.isRewarded, isPopped: true }
          : idea
      );

      // Move the idea with isPopped = true to the top
      const poppedIdea = updatedIdeas.find((idea) => idea.isPopped);
      const otherIdeas = updatedIdeas.filter((idea) => !idea.isPopped);

      // Reorder and set the new state
      return [poppedIdea, ...otherIdeas];
    });

    // After the animation is completed, reset the pop state after 1s (duration of the animation)
    setTimeout(() => {
      setIdeas((prevIdeas) =>
        prevIdeas.map((idea) =>
          idea.isPopped ? { ...idea, isPopped: false } : idea
        )
      );
    }, 1000); // Matches the duration of the "pop" animation
  };

  return (
    <>
      <div className="container mx-auto p-4 bg-indigo-600 ">
        <h1 className="text-3xl font-bold text-white mb-6">
          Manager Dashboard
        </h1>

        <div className="space-y-6">
          {/* Loop through each idea */}
          {ideas.map((idea) => (
            <div
              key={idea._id}
              className={`flex flex-col p-6 border rounded-md shadow-md bg-white mb-4 ${
                idea.isPopped ? "animate-pop" : ""
              }`}
              style={{ transition: "all 0.5s ease-out" }}
            >
              {/* Left: Idea details */}
              <div className="flex-1">
                <h2 className="text-xl font-semibold">{idea.employeeName}</h2>
                <p className="text-md mt-2">{idea.idea}</p>

                {/* Department with indigo background and bold font */}
                <span
                  className="inline-block mt-2 px-3 py-1 text-white rounded-full font-bold"
                  style={{ backgroundColor: "#4C51BF" }} // indigo-600 background
                >
                  {idea.department}
                </span>
              </div>

              {/* Right: Star button and AI Filtered label */}
              <div className="flex flex-col items-center justify-center mt-4">
                <button
                  onClick={() => handleReward(idea._id)}
                  className={`text-white ${
                    idea.isRewarded ? "bg-yellow-500" : "bg-gray-300"
                  } text-3xl p-4 rounded-full transition duration-300 ease-in-out transform`}
                >
                  <FaStar />
                </button>
                <span className="text-lg font-bold text-gray-700 mt-2">
                  {idea.votes} Votes
                </span>

                {/* AI Filtered label */}
                <span
                  className="inline-block mt-2 px-3 py-1 text-white rounded-full font-bold"
                  style={{
                    backgroundColor: idea.isAIFiltered ? "#4C51BF" : "#E53E3E", // Indigo for AI filtered, red for not
                  }}
                >
                  {idea.isAIFiltered ? "AI Filtered" : "Not AI Filtered"}
                </span>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* CSS for animation */}
      <style jsx>{`
        @keyframes popAnimation {
          0% {
            transform: translateY(0); /* Start at original position */
          }
          50% {
            transform: translateY(-30px); /* Move up 30px */
          }
          100% {
            transform: translateY(0); /* Return to original position */
          }
        }

        .animate-pop {
          animation: popAnimation 1s ease-out;
        }
      `}</style>
    </>
  );
};

export default Manager;
